import React,{useContext,useState} from "react";
import { Card,  CardBody, CardFooter, Image, Stack, Heading,Text,Divider,ButtonGroup,Button } from '@chakra-ui/react'
import './style.css';
import { CartContext } from "../../context/carritoContext";

import { Link } from '@chakra-ui/react';



const CardItem = ({data}) => {


  const [cart,setCart] = useContext(CartContext)
  const addToCart = () =>{
      setCart((articles)=>{
          const artFound=articles.find((article)=> article.id===data.id)
          if (artFound){
              return articles.map((item)=>{
                  if (item.id === data.id){
                      return {...item,cant:item.cant+1};
                  }else{
                      return item
                  }
              })
          }else{
              return [...articles,{data,cant:1,precio:data.precio,nombre:data.nombre}]
          }
      })
    }  
    return(    
      <div key={data.id}>  
      <Card maxW='sm'  className="card" >
      <Link to='/home'>
        <CardBody className="card-body">
        
          <Image
            src={`${data.img}`}
            alt='img'
            borderRadius='lg'
            className="card-img"
          />
          <Stack mt='6' spacing='3'>
            <Heading size='md'>{data.nombre}</Heading>
            <Text color='blue.600' fontSize='2xl'>
              $ {data.precio}
            </Text>
          </Stack>
        
        </CardBody>
        
        <Divider />
        </Link>
        <CardFooter>
          <ButtonGroup ml='2' spacing='3'>
            <Button variant='solid' colorScheme='blue' onClick={()=>addToCart()}>
              Comprar
            </Button>
            <Button variant='ghost' colorScheme='blue' onClick={()=>addToCart()}>
              Agregar al carrito
            </Button>
          </ButtonGroup>
        </CardFooter>
      </Card>
      </div>
  )
}

export default CardItem
