import React ,{useContext}from "react";
import { CartContext } from "../../context/carritoContext";
import { Tr, Td,Button} from '@chakra-ui/react'

const Itemshop = ({data})=>{ 
  const [cart,setCart] = useContext(CartContext);
  const sumaArticulo=(id)=>{
      setCart((articles)=>{
          if (articles.find((article)=> article.id===id)){
              return cart.map((item)=>{
                  if (item.id === id){
                      const cant=item.cant+1
                      console.log({...item})
                      return {...item,cant:cant,precio:item.precio*cant}
                  }
                  else{
                    return item
                  }
              })
          }
      })
    } 
    const restaArticulo=(id)=>{
      setCart(()=>{
        if (cart.find((article)=> article.id===id)){
          return cart.map((item)=>{
            const cant=Math.max(0, item.cant-1)            
            if (item.id===id){
              if (cant>0){
                return {...item,cant:cant,precio:item.precio*cant}
              }  
            }else{
              return item
            } 
          })
        }else{
          return cart
        }
      })
    }
    return(
    <>
      <Tr>
        <Td>{data.nombre}</Td>
        <Td>
          <Button colorScheme='blackAlpha'  borderRadius={'15px'} size="xs" onClick={()=>{sumaArticulo(data.id)}}>+</Button>
          {data.cant} 
          <Button colorScheme='blackAlpha' borderRadius={'15px'} size="xs" onClick={()=>{restaArticulo(data.id)}}>-</Button>
        </Td>
        <Td isNumeric>$ {data.precio}</Td>
      </Tr>
    </>    
    )
}

export default Itemshop
