import React from 'react'
import './style.css'
import Carrito from '../carrito'
import useMediaQuery from '../../hooks'

const useIsMobile= () => {
    return (useMediaQuery('(max-width: 1109px)'))}

const NavbarItems = ({items})=>{
    return(
        <>
            {items.map(item =>
                     <li className="nav-item active">
                     <a className="nav-link" href="#">{item}</a></li>)}
            {useIsMobile?
                        <Carrito/>          
                    :
                        <li className="nav-item active">
                        <a className="nav-link" href="#">Carrito</a></li>}

            
        </>
    )}


export default NavbarItems
