import React ,{useContext}from "react";
import { CartContext } from "../../context/carritoContext";
import { Tr, Td,Button} from '@chakra-ui/react'

const Itemshop = ({data})=>{ 
  const [cart,setCart] = useContext(CartContext);
  const sumaArticulo=()=>{
      setCart((articles)=>{
          if (articles.find((article)=> article.id===data.id)){
              return articles.map((item)=>{
                  if (item.id === data.id){
                      console.log(data.id)
                      return {...item,cant:item.cant+1}
                  }
                  else{
                    return item
                  }
              })
          }
      })
    } 
    const restaArticulo=(id)=>{
      setCart((articles)=>{
        if (articles.find((article)=> article.id===id && article.cant===1)){
          return articles.filter((item)=>item.id!==id)
        }else{
          return articles.map((item)=>{
            if (item.id===id){
              return {...item, cant:item.cant-1 }    
            }else{
              return item
            }
          })
        }
      })
    }
    return(
    <>
      <Tr>
        <Td>{data.nombre}</Td>
        <Td>
          <Button colorScheme='blackAlpha'  borderRadius={'15px'} size="xs" onClick={()=>{sumaArticulo()}}>+</Button>
          {data.cant} 
          <Button colorScheme='blackAlpha' borderRadius={'15px'} size="xs" onClick={()=>{restaArticulo()}}>-</Button>
        </Td>
        <Td isNumeric>$ {data.precio * data.cant}</Td>
      </Tr>
    </>    
    )
}

export default Itemshop
