import React from 'react'
import './style.css'
import Carrito from '../carrito'
import Buttonnavbar from '../button-navbar'
import { useState } from 'react'




const NavbarItems = ({items})=>{
    const isMobile= window.matchMedia("(max-width: 980px)").matches
    const [title,setTitle] = useState('')

    return(
        <>
            {console.log(isMobile)}
            {items.map(item =>
                     <li className="nav-item">
                     <Buttonnavbar event={setTitle(item)} tittle={item}/>
                     <a className="nav-link" aria-current="page" href="#">{item}</a></li>)}
            {!isMobile  ?
                        <Carrito/>          
                        :
                        <li className="nav-item">
                        <Buttonnavbar event=""  tittle="Carrito" />    
                        <a className="nav-link" aria-current="page" href="#">Carrito</a></li>}
                  
        </>
    )}


export default NavbarItems
