import React,{useContext} from "react";
import './style.css';
import { CartContext } from "../../context/carritoContext";
import Itemshop from "../itemshop/itemshop";
import {
    Drawer,
    DrawerBody,
    DrawerFooter,
    DrawerHeader,
    DrawerOverlay,
    DrawerContent,
    DrawerCloseButton,
    useDisclosure,
    Button,
    Table,
    Thead,
    Tbody,
    Tfoot,
    Tr,
    Th,
    Td,
    TableContainer
  } from '@chakra-ui/react'




const Carrito = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const btnRef = React.useRef()    
    const [cart,setCart] = useContext(CartContext);
    console.log(cart)
    const cantTotal= cart.reduce((cant, item)=>{
        return cant+item.cant;
    },0)

    const total =cart.reduce((total, item)=>{
        return total+item.precio;
    },0) 

    return (
        <li className="nav-item">
            <button ref={btnRef} colorScheme='teal' onClick={onOpen} id="btnCarro" className="btn bi-cart-fill" ></button> 
            {cantTotal > 0 && <p id="btnCount" className="btnCount" href="#">{cantTotal>99?"+99":cantTotal}</p>}           
            <Drawer
                isOpen={isOpen}
                placement='right'
                onClose={onClose}
                finalFocusRef={btnRef}
                size="lg"
            >
                <DrawerOverlay />
                <DrawerContent>
                <DrawerCloseButton />
                <DrawerHeader>Carrito de compra</DrawerHeader>

                <DrawerBody>
                   {cantTotal>0?
                    <TableContainer overflowX="hidden">
                    <Table size='sm'>
                        <Thead>
                        <Tr>
                            <Th>Articulo</Th>
                            <Th>Cantidad</Th>
                            <Th isNumeric>Subtotal</Th>
                            
                        </Tr>
                        </Thead>
                        <Tbody>
                         {cart.map((item)=>{
                            return( 
                                <Itemshop data={item} />)                                
                         })}   
                        </Tbody>
                        <Tfoot>
                        <Tr>
                            <Th>Total</Th>
                            <Th isNumeric>$ {total}</Th>
                        </Tr>
                        </Tfoot>
                    </Table>
                    </TableContainer>
                   :"Ups!,el carrito esta vacío"} 
                </DrawerBody>

                <DrawerFooter>
                    <Button colorScheme='blue' isDisabled={cantTotal===0}>Finalizar compra</Button>
                </DrawerFooter>
                </DrawerContent>
            </Drawer>            

        </li>
    )
}

export default Carrito
