import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import './style.css';
import { Card,  CardBody, CardFooter, Image, Stack, Heading,Text,ButtonGroup,Button } from '@chakra-ui/react'
import imagenNoDisponible from '../../img/imagenNoDisponible.jpg'
import {db} from "../../firebase/firebaseConfig";
import { doc, getDoc } from "firebase/firestore";
const CardDetail=()=>{
    const [product,setProduct] = useState({});
    let {id} = useParams()
    
    
    useEffect(()=>{
        const getProducto= async () => { 
            const docRef = doc(db, "id", id)
            const docSnap = await getDoc(docRef);

            if (docSnap.exists()) {
              console.log("Document data:", docSnap.data());
            } else {
              // doc.data() will be undefined in this case
              console.log("No such document!");
            }
            setProduct( docSnap.data())
        };
        getProducto();
     
    },[id])
    return(
        <div> 
            <div key={product.id}  className='card-detail' >
                <Card
                    direction={{ base: 'column', sm: 'row' }}
                    overflow='hidden'
                    variant='outline'
                    >
                    <Image
                        objectFit='cover'
                        maxW={{ base: '100%', sm: '200px' }}
                        src={product.img === undefined?imagenNoDisponible :product.img}
                        alt='Caffe Latte'
                    />

                    <Stack>
                        <CardBody>
                        <Heading size='md'>{product.nombre}</Heading>

                        <Text py='2'>
                            {product.descripcion}
                        </Text>
                        </CardBody>

                        <CardFooter>
                        <Button variant='solid' colorScheme='blue'>
                            Comprar
                        </Button>
                        </CardFooter>
                    </Stack>
                    </Card>
            </div>
        </div>    

    )
}

export default CardDetail;
