import React,{useEffect,useState} from "react";
import './style.css';
import { doc, setDoc } from "firebase/firestore";
import {db} from "../firebase/firebaseConfig";
import { FormControl, 
        FormLabel, 
        Input,
        FormHelperText,
        useToast,
        Button,} from '@chakra-ui/react'



const CheckShop= () =>{
    const [input, setInput] = useState('')
    const [error,setError]= useState(false)
    const toast = useToast()
    const handleInputChange = (e) => {
        setInput(e.target.value)}

    const postNewslatter=async ()=>{
        await setDoc(doc(db, "postNewslatter", input), {
            email:input,
          });
    }
    const suscribir = ()=>{
        if (!input.includes('@')){
            setError(true)   
        }else{
            setInput('')
            setError(false)
            postNewslatter()
            toast({
                title: 'Suscripto exitosamente',
                status: 'info',
                variant: 'top-accent',
                duration: 1000,
                isClosable: true,
              })
        }       
    }      
    return(
        <div className="checkshop">
            <h1>Ingrese sus datos para fianlizar la compra</h1>
            <FormControl isRequired>
                <FormLabel>Nombre</FormLabel>
                <Input placeholder='Nombre' />
                <FormLabel>Apellido</FormLabel>
                <Input placeholder='Apellido' />
                <FormLabel>Direccion</FormLabel>
                <Input placeholder='Calle 1234' />
                <FormLabel className="newslatter-label">Email</FormLabel>
                <Input type='email' value={input} onChange={handleInputChange} colorScheme='white' className="newslatter-input" placeholder='Email@email.com' />
                {error && (
                    <FormHelperText>
                    Ingrese un e-mail valido 
                    </FormHelperText>
                ) }
                <Button
                    mt={4}
                    variant='solid' 
                    colorScheme='blue'
                    type='submit'
                    onClick={()=>suscribir()}
                >
                    Confirmar compra
                </Button> 

            </FormControl>
        </div>
    )
}

export default CheckShop
