import React,{useContext,useState} from "react";
import './style.css';
import { doc, setDoc } from "firebase/firestore";
import {db} from "../firebase/firebaseConfig";
import { CartContext } from "../context/carritoContext";
import { FormControl, 
        FormLabel, 
        Input,
        FormHelperText,
        useToast,
        ButtonGroup,
        Button,} from '@chakra-ui/react'
import ItemListCart from "../components/itemListCart/itemListCart";



const CheckShop= () =>{
    const [input, setInput] = useState('')
    const [error,setError]= useState(false)
    const [cart,setCart] = useContext(CartContext);
  
    const handleInputChange = (e) => {
        setInput(e.target.value)}

    const postSendShop=async ()=>{
        await setDoc(doc(db, "postNewslatter", input), {
            email:input,
          });
    }
    const cancelShop=()=>{
        setCart([])

    }
    const sendShop = ()=>{
        if (!input.includes('@')){
            setError(true)   
        }else{
            setInput('')
            setError(false)
            postSendShop()
        }       
    }      
    return(
        <div className="contenedor-checkshop">
            <div className="checkshop">
            <h1>Ingrese sus datos para fianlizar la compra</h1>
            <FormControl isRequired>
                <FormLabel>Nombre</FormLabel>
                <Input placeholder='Nombre' onChange={handleInputChange}/>
                <FormLabel>Apellido</FormLabel>
                <Input placeholder='Apellido' onChange={handleInputChange}/>
                <FormLabel>Direccion</FormLabel>
                <Input placeholder='Calle 1234' onChange={handleInputChange}/>
                <FormLabel className="checkshop-label">Email</FormLabel>
                <Input type='email' value={input} onChange={handleInputChange}   placeholder='Email@email.com' />
                {error && (
                    <FormHelperText>
                    Ingrese un e-mail valido 
                    </FormHelperText>
                ) }
                <ButtonGroup ml='2' spacing='3' >
                <Button
                    mt={4}
                    variant='solid' 
                    colorScheme='blue'
                    type='submit'
                    onClick={()=>sendShop()}
                >
                    Confirmar compra
                </Button>
                <Button
                    mt={4}
                    variant='solid' 
                    colorScheme='blue'
                    type='submit'
                    onClick={()=>cancelShop()}
                >
                    Cancelar compra
                </Button>  
                </ButtonGroup>      
            </FormControl>
            </div>
            <div>
                <h1>Resumen de compra</h1>
                <ItemListCart endshop={true} />
            </div>
        </div>
    )
}

export default CheckShop
