import React,{useContext,useState} from "react";
import './style.css';
import { addDoc, setDoc,collection } from "firebase/firestore";
import {db} from "../firebase/firebaseConfig";
import { CartContext } from "../context/carritoContext";
import { FormControl, 
        FormLabel, 
        Input,
        FormHelperText,
        ButtonGroup,
        Button,} from '@chakra-ui/react'
import ItemListCart from "../components/itemListCart/itemListCart";
import { Link } from "react-router-dom";

const initialState = {
    name: "",
    lastName: "",
    direction: "",
    email: "",
    shop: [],
  };

const CheckShop= () =>{
    const [values, setValues] = useState(initialState);
    const [purchaseID, setPurchaseID] = useState("");
    const [input, setInput] = useState('')
    const [error,setError]= useState(false)
    const [cart,setCart] = useContext(CartContext);

    const handleInputChange = (e) => {
    const { value, name } = e.target;
    setValues({ ...values, [name]: value, shop:cart });
    };
    const postSendShop=async (e)=>{
        e.preventDefault();
        // Add a new document with a generated id.
        const docRef = await addDoc(collection(db, "compras"), {
          values,
        });
        // console.log("Document written with ID: ", docRef.id);
        setPurchaseID(docRef.id);
        setValues(initialState);
        setCart([])
      };

    const cancelShop=()=>{
        setCart([])
    }

    const sendShop = ()=>{
        if (!input.includes('@')){
            setError(true)   
        }else{
            setInput('')
            setError(false)
            postSendShop()
        }       
    }      
    return(
        <div className="contenedor-checkshop">
            <div className="checkshop">
            <h1>Ingrese sus datos para fianlizar la compra</h1>
            <FormControl isRequired>
                <FormLabel>Nombre</FormLabel>
                <Input  name="name" placeholder='Nombre' onChange={handleInputChange}/>
                <FormLabel>Apellido</FormLabel>
                <Input  name="lastName" placeholder='Apellido' onChange={handleInputChange}/>
                <FormLabel>Direccion</FormLabel>
                <Input name="direction" placeholder='Calle 1234' onChange={handleInputChange}/>
                <FormLabel className="checkshop-label">Email</FormLabel>
                <Input name="email" type='email' value={input} onChange={handleInputChange}   placeholder='Email@email.com' />
                {error && (
                    <FormHelperText>
                    Ingrese un e-mail valido 
                    </FormHelperText>
                ) }
                <ButtonGroup ml='2' spacing='3' >
                <Link to="/">
                <Button
                    mt={4}
                    variant='solid' 
                    colorScheme='blue'
                    type='submit'
                    onClick={()=>sendShop()}
                >
                    Confirmar compra
                </Button>
                </Link>
                <Link to="/">
                <Button
                    mt={4}
                    variant='solid' 
                    colorScheme='blue'
                    type='submit'
                    onClick={()=>cancelShop()}
                >
                    Cancelar compra
                </Button>
                </Link>  
                </ButtonGroup>      
            </FormControl>
            </div>
            <div>
                <h1>Resumen de compra</h1>
                <ItemListCart endshop={true} />
            </div>
        </div>
    )
}

export default CheckShop
