import logo from './logo.svg';
import './App.css';
import Button from './components/button';
import Modal from './components/modal';
import { useState } from 'react';/*hook - permite modificar el estado de un componente*/


function App() {
  const [modalOpen, setModalOpen]= useState(false)

  const eventoClick = () =>{setModalOpen(true)} 

  return (
    <div className="App">
      <header className="App-header">
        <Button tittle="pepe" event={eventoClick}></Button>
        <Modal isOpen={modalOpen} actionModal={()=>setModalOpen(false)}>
          <p>contenido modal</p>
        </Modal>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

      </header>
    </div>
  );
}

export default App;
