import './App.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
/*HOOKS*/
import { useState } from 'react';/*hook - permite modificar el estado de un componente*/

/*COMPONENTS*/
import NavBar from './components/navbar';
import Modal from './components/modal';
import Home from './pages/Home';
import Vinos from './pages/Vinos';
import Cervezas from './pages/Cervezas';
import Whiskies from './pages/Whiskies';
import Spirits from './pages/Spirits';
import Promociones from './pages/Promociones';
import CardDetail from './components/card-detail';

function App() {
  return (
    <BrowserRouter>
      <div className="App">    

        <NavBar/>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/Vinos" element={<Vinos />} />
          <Route path="/Whiskies" element={<Whiskies />} />
          <Route path="/Cervezas" element={<Cervezas />} />
          <Route path="/Spirits" element={<Spirits />} />
          <Route path="/Promociones" element={<Promociones />} />
          <Route path="/Detalle/:id" element={<CardDetail />} />       
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
