import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import './style.css';
import { Card,  CardBody, CardFooter, Image, Stack, Heading,Text,ButtonGroup,Button } from '@chakra-ui/react'
import imagenNoDisponible from '../../img/imagenNoDisponible.jpg'
import {db} from "../../firebase/firebaseConfig";
import { collection, query, where, getDocs } from "firebase/firestore";
const CardDetail=()=>{
    const [product,setProduct] = useState({});
    let {id} = useParams()
    
    
    useEffect(()=>{
        const getProducto= async () => { 
            const q = query(collection(db, "bebidas"),where("id","==",id));
            const querySnapshot = await getDocs(q);
            const docs = [];
           // console.log(querySnapshot)
            querySnapshot.forEach((doc) => {
                docs.push({...doc.data(), id:doc.id})
            });
            setProduct(docs)
        };
        getProducto();
     
    },[id])
    return(
        <div> 
            <div key={product.id}  className='card-detail' >
                <Card
                    direction={{ base: 'column', sm: 'row' }}
                    overflow='hidden'
                    variant='outline'
                    >
                    <Image
                        objectFit='cover'
                        maxW={{ base: '100%', sm: '200px' }}
                        src={product.images === undefined?imagenNoDisponible :product.images[0]}
                        alt='Caffe Latte'
                    />

                    <Stack>
                        <CardBody>
                        <Heading size='md'>{product.title}</Heading>

                        <Text py='2'>
                            {product.description}
                        </Text>
                        </CardBody>

                        <CardFooter>
                        <Button variant='solid' colorScheme='blue'>
                            Comprar
                        </Button>
                        </CardFooter>
                    </Stack>
                    </Card>
            </div>
        </div>    

    )
}

export default CardDetail;
