import React,{useContext} from "react";
import './style.css';
import { CartContext } from "../../context/carritoContext";
import {
    Drawer,
    DrawerBody,
    DrawerFooter,
    DrawerHeader,
    DrawerOverlay,
    DrawerContent,
    DrawerCloseButton,
    useDisclosure,
    Button
  } from '@chakra-ui/react'


const Carrito = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const btnRef = React.useRef()    
    const [cart,setCart] = useContext(CartContext);
    const cantTotal= cart.reduce((cant, item)=>{
        return cant+item.cant;
    },0)
    return (
        <li className="nav-item">
            <button id="btnCarro" className="btn bi-cart-fill" ></button> 
            {cantTotal > 0 && <p id="btnCount" className="btnCount" href="#">{cantTotal}</p>}           
            <Drawer
                isOpen={isOpen}
                placement='right'
                onClose={onClose}
                finalFocusRef={btnRef}
            >
                <DrawerOverlay />
                <DrawerContent>
                <DrawerCloseButton />
                <DrawerHeader>Carrito de compra</DrawerHeader>

                <DrawerBody>
                    
                </DrawerBody>

                <DrawerFooter>
                    <Button variant='outline' mr={3} onClick={onClose}>
                    Cancel
                    </Button>
                    <Button colorScheme='blue'>Save</Button>
                </DrawerFooter>
                </DrawerContent>
            </Drawer>            

        </li>
    )
}

export default Carrito
